import { Component, Input, OnInit } from '@angular/core';
import { StudentService } from '../student.service';

interface StudentData{
  SubmittedAnswerId : Number,
ExerciseId: Number
Difficulty: Number,
Subject: String,
LearningObjective: String,
Progress:Number,
Percent ?: Number,
Color ?: String
 }


@Component({
  selector: 'app-student-details',
  templateUrl: './student-details.component.html',
  styleUrls: ['./student-details.component.css']
})
export class StudentDetailsComponent implements OnInit{
newdata:StudentData[]= [];
currentD = new Date('2015-03-02');

@Input() data:any;
constructor(private studentService: StudentService){
}
getToday(): string {
  return new Date('2015-03-02').toISOString().split('T')[0]
}
showDetails(data: any,newDate: any){
  this.studentService.getStudentDetails(data,newDate).subscribe((result:any)=>{
    this.newdata = [];
    result.map((data:StudentData)=>{
      console.log(data)
      data.Color = "#4CAF50";
      if(Number(data.Progress)<0){
        data.Percent = (Number(data.Progress)*-1);
        data.Color = "red";
      }else{
        data.Percent = Number(data.Progress);
      }
      this.newdata.push(data)
    })
  });
}



onDateChnge(){
   console.log(this.currentD)
   this.showDetails(this.data,this.currentD);
}
ngOnChanges(){
  console.log(`Inside details ${this.data}`)
  this.showDetails(this.data,this.currentD);
}
ngOnInit(): void {
  console.log(this.data)

}

}
