import { Component, OnInit, EventEmitter, Output } from '@angular/core';
import { StudentService } from '../student.service';
@Component({
  selector: 'app-student',
  templateUrl: './student.component.html',
  styleUrls: ['./student.component.css'],
})
export class StudentComponent implements OnInit {
  constructor(private studentService: StudentService) {}
  @Output() updatedEvent = new EventEmitter<number>();
  @Output() currentStudentData = new EventEmitter<number>();
  students: number[] = [];
  currentStudent: number = 0;
  activeStudent: Number = 0;
  public discoverClicked(student: number) {
    this.activeStudent = student;
    this.updatedEvent.emit(student);
  }
  ngOnInit() {
    this.studentService.getStudents().subscribe((result: any) => {
      result.map((data: number, index: number) => {
        if (index == 0) {
          this.currentStudent = data;
          this.currentStudentData.emit(data);
        }
        this.students.push(data);
      });
    });
  }
}
<div class="center">
  <section class="todo-cmp">
    <header class="todo-cmp__header">
      <h2>Student List</h2>
    </header>
    <div class="studentsList">
      <ul *ngFor="let student of students" class="todo-cmp__list">
        <li [class.active]="student == activeStudent">
          <label for="todo-01" (click)="discoverClicked(student)">
            <span>{{ student }}</span>
          </label>
        </li>
      </ul>
    </div>
  </section>
</div>
