import { Component} from '@angular/core';
//import { StudentService } from './student.service'
interface StudentData{
  SubmittedAnswerId : Number,
ExerciseId: Number
Difficulty: Number,
Subject: String,
LearningObjective: String
 }

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})


export class AppComponent {
  title = '';
 /* constructor(private studentService: StudentService){

  }
*/

 // newdata:StudentData[]= [];
  studentCurrent: Number = 0;

  gotData($event: number){
    this.studentCurrent = $event;

/*
    this.studentService.getStudentDetails($event).subscribe((result:any)=>{
      this.newdata = [];
      result.map((data:StudentData)=>{
        this.newdata.push(data)
      })
    });*/
    /*for(let i =0;i<=10;i++){
      this.newdata.push($event)
      console.log(this.newdata)
    }*/
  }
}
<section>
  <h1>Snappet Class</h1>
  <div class="frame">
    <div class="col-md-4">
      <app-student (updatedEvent)="gotData($event)"></app-student>
    </div>

    <app-student-details data="{{ studentCurrent }}"></app-student-details>
  </div>
</section>
