import { Component} from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = '';

  newdata:number[]= [];
  gotData($event: number){
    for(let i =0;i<=10;i++){
      this.newdata.push($event)
      console.log(this.newdata)
    }
  }
}
